
// JAVA While Loop

public class Statement_13 {
    public static void main(String[] args) {
        // while (condition) {
        //     //looping statement;
        // }

        // TODO Auto-generated method stub
        int i=0;
        System.out.println("Printing the list of first 10 even numbers\n");
        while (i<=10) {
            System.out.println(i);
            i=i+2;
        }
    }
}
